import React from "react";
import BreadCrumb from "../../components/BreadCrumb/BreadCrumb";
import tickets from '../../assets/data/dummyTicket.json'

const ticket = tickets[0]

const Ticket = () => {
  return (
    <div className="container">
      <div>
        <BreadCrumb page="Ticket" />
      </div>
      <div className="flex justify-between">
        <div>
          <div className="font-bold">Subject: <span className="font-normal">{ticket.subject}</span></div>
          <div className="font-bold">Date: <span>{ticket.addedAt}</span></div>
          <div className="font-bold">status: <span>{ticket.status}</span></div>
        </div>
        <div>
          <button
            type="button"
            class="py-2.5 px-5 mr-2 mb-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-200 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700"
          >
            Close Ticket
          </button>
        </div>
      </div>
      <div></div>
    </div>
  );
};

export default Ticket;
