import React from "react";

const MessageHistory = (props) => {
  const { messages } = props;

  console.log(messages);
  if (!messages) return null;
  return messages.map((message, i) => (
    <div
      key={i}
      className={
        message.messageBy === "client"
          ? "mt-5 flex flex-row justify-between"
          : "mt-5 flex flex-row-reverse justify-between"
      }
    >
      <div className="send">
        <div className="sender">{message.messageBy}</div>
        <div className="date">{message.date}</div>
      </div>
      <div className="message w-4/5 border p-4">{message.message}</div>
    </div>
  ));
};

export default MessageHistory;
