import React, { useEffect } from "react";
import { useState } from "react";
import BreadCrumb from "../../components/BreadCrumb/BreadCrumb";
import NewTicketForm from "../../components/NewTicketForm/NewTicketForm";
import { shortText } from "../../utils/validation";

const initialFormData = {
  subject: "",
  issueDate: "",
  details: "",
};

const initialFormError = {
  subject: false,
  issueDate: false,
  details: false,
};

const NewTicket = () => {
  const [formData, setFormData] = useState(initialFormData);
  const [formDataError, setFormDataError] = useState(initialFormError);

  const onChangeHandler = (e) => {
    const { name, value } = e.target;

    setFormData({
      ...formData,
      [name]: value,
    });
    console.log(name, value);
  };

  const onSubmitHandler = async (e) => {
    e.preventDefault();

    formDataError(initialFormError)

    const isSubjectValid = await shortText(formData.subject)

        setFormDataError({
            ...formDataError,
            subject: !isSubjectValid,
        })

    console.log("Form Submit received");
  };

  useEffect(() => {}, [formData, formDataError]);
  return (
    <div className="container">
      <div className="mb-8">
        <BreadCrumb page="New Ticket" />
      </div>
      <div>
        <div className="shadow-2xl">
          <NewTicketForm
            onChangeHandler={onChangeHandler}
            formData={formData}
            onSubmitHandler={onSubmitHandler}
            formDataError={formDataError}
          />
        </div>
      </div>
    </div>
  );
};

export default NewTicket;
